function createObserver(e, t, r) {
  const s = document.querySelectorAll(e),
    i = new IntersectionObserver((e, t) => {
      e.forEach((e) => {
        e.isIntersecting && (r(e), t.unobserve(e.target));
      });
    }, t);
  s.forEach((e) => i.observe(e));
}
const imageOptions = { threshold: 0.25, margin: "0px 0px 100px 0px" };
createObserver(".image", imageOptions, (e) => {
  e.target.classList.add("slide-from-left");
});
const titleOptions = { threshold: 0.15, margin: "0px 0px 100px 0px" };
createObserver(".title", titleOptions, (e) => {
  e.target.classList.add("opacity-title");
});
const listOptions = { threshold: 0.15, margin: "0px 0px 100px 0px" };
createObserver(".article__item", listOptions, (e) => {
  e.target.classList.add("slide-from-right");
});
const swiper = new Swiper(".swiper", {
  direction: "horizontal",
  slidesPerView: 5,
  autoplay: { delay: 0 },
  speed: 5e3,
  allowTouchMove: !1,
  loop: !0,
  breakpoints: { 900: { height: 0 } },
});
